// --- White noise (WebAudio) implementation ---
// Creates a white-noise buffer and plays it in a loop through a gain node.
(function(){
  var AudioContext = window.AudioContext || window.webkitAudioContext;
  if(!AudioContext) return; // no webaudio support

  var ctx = null;
  var noiseNode = null; // BufferSource
  var noiseGain = null;
  var isPlaying = false;

  function createNoise(){
    if(!ctx) ctx = new AudioContext();
    // buffer: 2 seconds mono
    var bufferSize = ctx.sampleRate * 2;
    var buffer = ctx.createBuffer(1, bufferSize, ctx.sampleRate);
    var data = buffer.getChannelData(0);
    for(var i=0;i<bufferSize;i++){
      data[i] = Math.random()*2 - 1;
    }
    var source = ctx.createBufferSource();
    source.buffer = buffer;
    source.loop = true;

    var gain = ctx.createGain();
    gain.gain.value = 0.25; // default volume (can be adjusted)

    source.connect(gain).connect(ctx.destination);

    return {source: source, gain: gain};
  }

  function startNoise(){
    if(isPlaying) return;
    var parts = createNoise();
    noiseNode = parts.source;
    noiseGain = parts.gain;
    try{
      if(ctx.state === 'suspended') ctx.resume();
    }catch(e){}
    noiseNode.start(0);
    isPlaying = true;
  }

  // stopNoise is retained internally but NOT exposed to UI (impossible to disable from the page)
  function stopNoise(){
    if(!isPlaying) return;
    try{ noiseNode.stop(0); }catch(e){}
    noiseNode.disconnect();
    noiseGain.disconnect();
    noiseNode = null; noiseGain = null; isPlaying = false;
  }

  // start-once handler: listens to first user gesture and starts noise permanently (no UI stop)
  function startOnce(){
    // create/resume context and start noise
    if(!ctx){
      try{ ctx = new AudioContext(); }catch(err){ console.warn('AudioContext failed', err); }
    }
    startNoise();

    // update UI: remove or update the note/button so user can't stop it
    var t = document.getElementById('noise-toggle');
    if(t){
      try{
        t.textContent = 'Bruit blanc activé';
        t.disabled = true;
        t.setAttribute('aria-pressed','true');
        // keep it visible but inert; optionally hide:
        // t.style.display = 'none';
      }catch(e){}
    }

    var note = document.getElementById('noise-note');
    if(note){ note.textContent = 'Bruit blanc activé (non désactivable depuis l\'interface)'; }

    // remove gesture listeners so this can't be triggered again
    document.removeEventListener('click', startOnce);
    document.removeEventListener('keydown', startOnce);
  }

  // attach when DOM ready
  document.addEventListener('DOMContentLoaded', function(){
    // listen for the first user gesture anywhere on the page
    document.addEventListener('click', startOnce);
    document.addEventListener('keydown', startOnce);

    // Keep visibility handling to be polite: suspend context when hidden, resume only if already playing
    document.addEventListener('visibilitychange', function(){
      if(!ctx) return;
      if(document.hidden){ if(ctx.state === 'running') ctx.suspend(); }
      else { if(ctx.state === 'suspended' && isPlaying) ctx.resume(); }
    });
  });

})();

